mapper.select:
select prod_name, prod_price, prod_brand, prod_from, prod_img
from   products
where  prod_no = #{prodNo}

select round(AVG(rev_star),1) 
from   reviews
where  prod_no = #{prodNo}

select count(rev_no) 
from   reviews
where  prod_no = #{prodNo}

select rev_star, rev_content, rev_date, mem_no
from   reviews
where  prod_no = #{prodNo}

select mem_addr
from members
where mem_name = #{memName}

mapper.update:
update members
set mem_addr = #{memAddr}
where mem_name = #{memName}
